import { defineConfig } from "vite";
import react from "@vitejs/plugin-react";
import themePlugin from "@replit/vite-plugin-shadcn-theme-json";
import path, { dirname } from "path";
import runtimeErrorOverlay from "@replit/vite-plugin-runtime-error-modal";
import { fileURLToPath } from "url";

const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);

export default defineConfig({
  plugins: [
    react(),
    runtimeErrorOverlay(),
    themePlugin(),
    ...(process.env.NODE_ENV !== "production" &&
    process.env.REPL_ID !== undefined
      ? [
          await import("@replit/vite-plugin-cartographer").then((m) =>
            m.cartographer(),
          ),
        ]
      : []),
  ],
  resolve: {
    alias: {
      "@": path.resolve(__dirname, "client", "src"),
      "@shared": path.resolve(__dirname, "shared"),
      "@assets": path.resolve(__dirname, "attached_assets"),
    },
  },
  root: path.resolve(__dirname, "client"),
  build: {
    outDir: path.resolve(__dirname, "dist/public"),
    emptyOutDir: true,
  },
});

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on port 5000
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = 5000;
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express, Request, Response, NextFunction } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { 
  contactSchema, 
  donationSchema, 
  memberSchema, 
  memberBenefitSchema, 
  memberDonationSchema, 
  memberPaymentSchema, 
  newsletterSchema, 
  volunteerSchema, 
  insertUserSchema 
} from "@shared/schema";
import nodemailer from "nodemailer";
import session from "express-session";
import memorystore from "memorystore";
import bcrypt from "bcryptjs";
import passport from "passport";
import { Strategy as GoogleStrategy } from "passport-google-oauth20";

// Extend the Session interface to include user property
declare module 'express-session' {
  interface Session {
    user?: {
      id: number;
      username: string;
      role: string;
    };
  }
}

// Type for authenticated requests
interface AuthenticatedRequest extends Request {
  user?: {
    id: number;
    username: string;
    role: string;
  }
}

export async function registerRoutes(app: Express): Promise<Server> {
  // Setup session middleware
  const MemoryStore = memorystore(session);
  app.use(
    session({
      cookie: { maxAge: 86400000 }, // 24 hours
      store: new MemoryStore({
        checkPeriod: 86400000 // prune expired entries every 24h
      }),
      resave: false,
      saveUninitialized: false,
      secret: process.env.SESSION_SECRET || "buddha-dhaam-secret"
    })
  );

  // Initialize Passport
  app.use(passport.initialize());
  app.use(passport.session());

  // Configure Google OAuth Strategy
  const GOOGLE_CLIENT_ID = process.env.GOOGLE_CLIENT_ID;
  const GOOGLE_CLIENT_SECRET = process.env.GOOGLE_CLIENT_SECRET;

  if (GOOGLE_CLIENT_ID && GOOGLE_CLIENT_SECRET) {
    passport.use(
      new GoogleStrategy(
        {
          clientID: GOOGLE_CLIENT_ID,
          clientSecret: GOOGLE_CLIENT_SECRET,
          callbackURL: "/api/auth/google/callback",
          scope: ["profile", "email"]
        },
        async (accessToken, refreshToken, profile, done) => {
          try {
            // Check if user exists with this Google ID
            let user = await storage.getUserByEmail(profile.emails?.[0]?.value || '');

            // If user doesn't exist, create a new one
            if (!user) {
              // Create a random username based on display name or id
              const username = `google_${profile.displayName?.replace(/\s+/g, '_').toLowerCase() || profile.id}`;

              // Create a new user
              user = await storage.createUser({
                username,
                // Generate a long random password since they'll be using Google OAuth
                password: Math.random().toString(36).slice(-16) + Math.random().toString(36).slice(-16),
                email: profile.emails?.[0]?.value,
                fullName: profile.displayName,
                googleId: profile.id,
                role: 'user'
              });
            }

            // Convert user to the format session expects
            return done(null, {
              id: user.id,
              username: user.username,
              role: user.role || 'user'
            });
          } catch (error) {
            return done(error as Error);
          }
        }
      )
    );
  }

  // Serialize and deserialize user for session management
  passport.serializeUser((user, done) => {
    done(null, user);
  });

  passport.deserializeUser((user, done) => {
    done(null, user as Express.User);
  });

  // Authentication middleware
  const authenticate = (req: AuthenticatedRequest, res: Response, next: NextFunction) => {
    if (req.session && req.session.user) {
      req.user = req.session.user;
      next();
    } else {
      res.status(401).json({ success: false, message: "Unauthorized" });
    }
  };

  // Authentication Routes

  // Register a new user
  app.post('/api/auth/register', async (req, res) => {
    try {
      // Validate the user data
      const userData = insertUserSchema.parse(req.body);

      // Check if username already exists
      const existingUser = await storage.getUserByUsername(userData.username);
      if (existingUser) {
        return res.status(400).json({ success: false, message: "Username already taken" });
      }

      // Check if email already exists
      if (userData.email) {
        const existingEmail = await storage.getUserByEmail(userData.email);
        if (existingEmail) {
          return res.status(400).json({ success: false, message: "Email already registered" });
        }
      }

      // Hash the password
      const salt = await bcrypt.genSalt(10);
      const hashedPassword = await bcrypt.hash(userData.password, salt);

      // Create the user with hashed password
      const user = await storage.createUser({
        ...userData,
        password: hashedPassword
      });

      // Return success without the password
      const { password, ...userWithoutPassword } = user;
      return res.status(201).json({
        success: true,
        message: "User registered successfully",
        user: userWithoutPassword
      });
    } catch (error: any) {
      console.error("Error registering user:", error);
      return res.status(400).json({ success: false, message: error.message });
    }
  });

  // Login
  app.post('/api/auth/login', async (req, res) => {
    try {
      const { username, password } = req.body;

      // Find the user
      const user = await storage.getUserByUsername(username);
      if (!user) {
        return res.status(400).json({ success: false, message: "Invalid username or password" });
      }

      // Verify password
      const isMatch = await bcrypt.compare(password, user.password);
      if (!isMatch) {
        return res.status(400).json({ success: false, message: "Invalid username or password" });
      }

      // Create session
      (req as any).session.user = {
        id: user.id,
        username: user.username,
        role: user.role || 'user'
      };

      // Return user info without password
      const { password: _, ...userWithoutPassword } = user;
      return res.status(200).json({
        success: true,
        message: "Login successful",
        user: userWithoutPassword
      });
    } catch (error: any) {
      console.error("Error logging in:", error);
      return res.status(400).json({ success: false, message: error.message });
    }
  });

  // Get current user
  app.get('/api/auth/me', authenticate, (req: AuthenticatedRequest, res) => {
    return res.status(200).json({
      success: true,
      user: req.user
    });
  });

  // Google OAuth routes
  if (process.env.GOOGLE_CLIENT_ID && process.env.GOOGLE_CLIENT_SECRET) {
    // Route to initiate Google OAuth flow
    app.get('/api/auth/google', passport.authenticate('google', { scope: ['profile', 'email'] }));

    // Google OAuth callback route
    app.get('/api/auth/google/callback', 
      passport.authenticate('google', { failureRedirect: '/login?error=google-auth-failed' }),
      (req, res) => {
        // If authentication is successful, set user in session
        if (req.user) {
          (req as any).session.user = req.user;
        }

        // Redirect to the client-side app
        res.redirect('/member-dashboard');
      }
    );
  }

  // Logout
  app.post('/api/auth/logout', (req, res) => {
    req.session.destroy((err) => {
      if (err) {
        return res.status(500).json({ success: false, message: "Could not log out" });
      }
      res.clearCookie('connect.sid');
      return res.status(200).json({ success: true, message: "Logged out successfully" });
    });
  });

  // Contact form submission
  app.post('/api/contact/send', async (req, res) => {
    try {
      const validatedData = contactSchema.parse(req.body);

      // Store the contact message
      const contactMessage = await storage.createContactMessage(validatedData);

      // Send email notification (mock implementation)
      const transporter = nodemailer.createTransport({
        host: "smtp.example.com",
        port: 587,
        secure: false,
        auth: {
          user: process.env.EMAIL_USER || "user@example.com",
          pass: process.env.EMAIL_PASSWORD || "password",
        },
      });

      // Email content
      const mailOptions = {
        from: '"Buddha Dhaam Website" <contact@buddhadhaam.org>',
        to: "info@buddhadhaam.org",
        subject: `New Contact: ${validatedData.subject || 'No Subject'}`,
        text: `
          Name: ${validatedData.name}
          Email: ${validatedData.email}
          Subject: ${validatedData.subject || 'No Subject'}

          Message:
          ${validatedData.message}
        `,
        html: `
          <p><strong>Name:</strong> ${validatedData.name}</p>
          <p><strong>Email:</strong> ${validatedData.email}</p>
          <p><strong>Subject:</strong> ${validatedData.subject || 'No Subject'}</p>
          <p><strong>Message:</strong></p>
          <p>${validatedData.message.replace(/\n/g, '<br>')}</p>
        `,
      };

      // In a real implementation, this would send the email
      // await transporter.sendMail(mailOptions);

      return res.status(200).json({ 
        success: true, 
        message: "Contact message sent successfully",
        id: contactMessage.id
      });
    } catch (error: any) {
      console.error("Error sending contact message:", error);
      return res.status(400).json({ success: false, message: error.message });
    }
  });

  // Newsletter subscription
  app.post('/api/newsletter/subscribe', async (req, res) => {
    try {
      const validatedData = newsletterSchema.parse(req.body);

      // Store the newsletter subscription
      const subscription = await storage.createNewsletter(validatedData);

      return res.status(200).json({ 
        success: true, 
        message: "Newsletter subscription successful",
        id: subscription.id
      });
    } catch (error: any) {
      console.error("Error subscribing to newsletter:", error);
      return res.status(400).json({ success: false, message: error.message });
    }
  });

  // Donation processing
  app.post('/api/donations/custom', async (req, res) => {
    try {
      const validatedData = donationSchema.parse(req.body);

      // Store the donation
      const donation = await storage.createDonation(validatedData);

      // In a real implementation, you would integrate with a payment gateway here

      return res.status(200).json({ 
        success: true, 
        message: "Donation processed successfully",
        id: donation.id,
        amount: donation.amount
      });
    } catch (error: any) {
      console.error("Error processing donation:", error);
      return res.status(400).json({ success: false, message: error.message });
    }
  });

  // Volunteer application
  app.post('/api/volunteer/apply', async (req, res) => {
    try {
      const validatedData = volunteerSchema.parse(req.body);

      // Store the volunteer application
      const application = await storage.createVolunteerApplication(validatedData);

      return res.status(200).json({ 
        success: true, 
        message: "Volunteer application submitted successfully",
        id: application.id
      });
    } catch (error: any) {
      console.error("Error submitting volunteer application:", error);
      return res.status(400).json({ success: false, message: error.message });
    }
  });

  // Member Portal API Routes

  // Create new member - must be authenticated
  app.post('/api/members', authenticate, async (req: AuthenticatedRequest, res) => {
    try {
      const validatedData = memberSchema.parse(req.body);

      // Store the new member
      const member = await storage.createMember(validatedData);

      return res.status(201).json({ 
        success: true, 
        message: "Member created successfully",
        member
      });
    } catch (error: any) {
      console.error("Error creating member:", error);
      return res.status(400).json({ success: false, message: error.message });
    }
  });

  // Get member by ID - authenticated
  app.get('/api/members/:id', authenticate, async (req: AuthenticatedRequest, res) => {
    try {
      const memberId = parseInt(req.params.id);
      if (isNaN(memberId)) {
        return res.status(400).json({ success: false, message: "Invalid member ID" });
      }

      const member = await storage.getMember(memberId);
      if (!member) {
        return res.status(404).json({ success: false, message: "Member not found" });
      }

      return res.status(200).json({ 
        success: true, 
        member
      });
    } catch (error: any) {
      console.error("Error fetching member:", error);
      return res.status(500).json({ success: false, message: error.message });
    }
  });

  // Get member by user ID - authenticated
  app.get('/api/members/user/:userId', authenticate, async (req: AuthenticatedRequest, res) => {
    try {
      const userId = parseInt(req.params.userId);
      if (isNaN(userId)) {
        return res.status(400).json({ success: false, message: "Invalid user ID" });
      }

      const member = await storage.getMemberByUserId(userId);
      if (!member) {
        return res.status(404).json({ success: false, message: "Member not found" });
      }

      return res.status(200).json({ 
        success: true, 
        member
      });
    } catch (error: any) {
      console.error("Error fetching member by user ID:", error);
      return res.status(500).json({ success: false, message: error.message });
    }
  });

  // Update member - authenticated
  app.patch('/api/members/:id', authenticate, async (req: AuthenticatedRequest, res) => {
    try {
      const memberId = parseInt(req.params.id);
      if (isNaN(memberId)) {
        return res.status(400).json({ success: false, message: "Invalid member ID" });
      }

      // Validate update data
      const validatedData = memberSchema.partial().parse(req.body);

      // Update the member
      const updatedMember = await storage.updateMember(memberId, validatedData);

      return res.status(200).json({ 
        success: true, 
        message: "Member updated successfully",
        member: updatedMember
      });
    } catch (error: any) {
      console.error("Error updating member:", error);
      return res.status(400).json({ success: false, message: error.message });
    }
  });

  // Get all members - restricted to admin
  app.get('/api/members', authenticate, async (req: AuthenticatedRequest, res) => {
    // Check if user is admin
    if (req.user?.role !== 'admin') {
      return res.status(403).json({ success: false, message: "Access denied" });
    }
    try {
      const members = await storage.getMembers();

      return res.status(200).json({ 
        success: true, 
        members
      });
    } catch (error: any) {
      console.error("Error fetching members:", error);
      return res.status(500).json({ success: false, message: error.message });
    }
  });

  // Get member benefits by membership level
  app.get('/api/member-benefits/:membershipLevel', async (req, res) => {
    try {
      const { membershipLevel } = req.params;

      const benefits = await storage.getMemberBenefits(membershipLevel);

      return res.status(200).json({ 
        success: true, 
        benefits
      });
    } catch (error: any) {
      console.error("Error fetching member benefits:", error);
      return res.status(500).json({ success: false, message: error.message });
    }
  });

  // Create member benefit
  app.post('/api/member-benefits', async (req, res) => {
    try {
      const validatedData = memberBenefitSchema.parse(req.body);

      // Store the new benefit
      const benefit = await storage.createMemberBenefit(validatedData);

      return res.status(201).json({ 
        success: true, 
        message: "Member benefit created successfully",
        benefit
      });
    } catch (error: any) {
      console.error("Error creating member benefit:", error);
      return res.status(400).json({ success: false, message: error.message });
    }
  });

  // Update member benefit
  app.patch('/api/member-benefits/:id', async (req, res) => {
    try {
      const benefitId = parseInt(req.params.id);
      if (isNaN(benefitId)) {
        return res.status(400).json({ success: false, message: "Invalid benefit ID" });
      }

      // Validate update data
      const validatedData = memberBenefitSchema.partial().parse(req.body);

      // Update the benefit
      const updatedBenefit = await storage.updateMemberBenefit(benefitId, validatedData);

      return res.status(200).json({ 
        success: true, 
        message: "Member benefit updated successfully",
        benefit: updatedBenefit
      });
    } catch (error: any) {
      console.error("Error updating member benefit:", error);
      return res.status(400).json({ success: false, message: error.message });
    }
  });

  // Get all member benefits
  app.get('/api/member-benefits', async (req, res) => {
    try {
      const benefits = await storage.getAllMemberBenefits();

      return res.status(200).json({ 
        success: true, 
        benefits
      });
    } catch (error: any) {
      console.error("Error fetching all member benefits:", error);
      return res.status(500).json({ success: false, message: error.message });
    }
  });

  // Member Recurring Donations API Routes

  // Get all recurring donations for a member
  app.get('/api/members/:memberId/donations', authenticate, async (req: AuthenticatedRequest, res) => {
    try {
      const memberId = parseInt(req.params.memberId);
      if (isNaN(memberId)) {
        return res.status(400).json({ success: false, message: "Invalid member ID" });
      }

      // Check if user has access to this member's data (admin or own data)
      const member = await storage.getMember(memberId);
      if (!member) {
        return res.status(404).json({ success: false, message: "Member not found" });
      }

      // Check if user is admin or the member belongs to the authenticated user
      if (req.user?.role !== 'admin' && member.userId !== req.user?.id) {
        return res.status(403).json({ success: false, message: "Access denied" });
      }

      const donations = await storage.getMemberDonations(memberId);

      return res.status(200).json({ 
        success: true, 
        donations
      });
    } catch (error: any) {
      console.error("Error fetching member donations:", error);
      return res.status(500).json({ success: false, message: error.message });
    }
  });

  // Get a specific recurring donation by ID
  app.get('/api/member-donations/:id', authenticate, async (req: AuthenticatedRequest, res) => {
    try {
      const donationId = parseInt(req.params.id);
      if (isNaN(donationId)) {
        return res.status(400).json({ success: false, message: "Invalid donation ID" });
      }

      // Get the donation
      const donation = await storage.getMemberDonation(donationId);
      if (!donation) {
        return res.status(404).json({ success: false, message: "Donation not found" });
      }

      // Get the member to check permissions
      const member = await storage.getMember(donation.memberId);
      if (!member) {
        return res.status(404).json({ success: false, message: "Member not found" });
      }

      // Check if user is admin or the member belongs to the authenticated user
      if (req.user?.role !== 'admin' && member.userId !== req.user?.id) {
        return res.status(403).json({ success: false, message: "Access denied" });
      }

      return res.status(200).json({ 
        success: true, 
        donation
      });
    } catch (error: any) {
      console.error("Error fetching member donation:", error);
      return res.status(500).json({ success: false, message: error.message });
    }
  });

  // Create a new recurring donation for a member
  app.post('/api/members/:memberId/donations', authenticate, async (req: AuthenticatedRequest, res) => {
    try {
      const memberId = parseInt(req.params.memberId);
      if (isNaN(memberId)) {
        return res.status(400).json({ success: false, message: "Invalid member ID" });
      }

      // Check if the member exists
      const member = await storage.getMember(memberId);
      if (!member) {
        return res.status(404).json({ success: false, message: "Member not found" });
      }

      // Check if user is admin or the member belongs to the authenticated user
      if (req.user?.role !== 'admin' && member.userId !== req.user?.id) {
        return res.status(403).json({ success: false, message: "Access denied" });
      }

      // Validate donation data
      const validatedData = memberDonationSchema.parse({
        ...req.body,
        memberId
      });

      // Create the donation
      const donation = await storage.createMemberDonation(validatedData);

      // In a real implementation, you would integrate with a payment gateway to set up recurring payments

      return res.status(201).json({ 
        success: true, 
        message: "Recurring donation created successfully",
        donation
      });
    } catch (error: any) {
      console.error("Error creating recurring donation:", error);
      return res.status(400).json({ success: false, message: error.message });
    }
  });

  // Update a recurring donation
  app.patch('/api/member-donations/:id', authenticate, async (req: AuthenticatedRequest, res) => {
    try {
      const donationId = parseInt(req.params.id);
      if (isNaN(donationId)) {
        return res.status(400).json({ success: false, message: "Invalid donation ID" });
      }

      // Get the existing donation
      const existingDonation = await storage.getMemberDonation(donationId);
      if (!existingDonation) {
        return res.status(404).json({ success: false, message: "Donation not found" });
      }

      // Get the member to check permissions
      const member = await storage.getMember(existingDonation.memberId);
      if (!member) {
        return res.status(404).json({ success: false, message: "Member not found" });
      }

      // Check if user is admin or the member belongs to the authenticated user
      if (req.user?.role !== 'admin' && member.userId !== req.user?.id) {
        return res.status(403).json({ success: false, message: "Access denied" });
      }

      // Validate the update data
      const validatedData = memberDonationSchema.partial().parse(req.body);

      // If memberId is provided, make sure it matches the existing donation's memberId
      if (validatedData.memberId && validatedData.memberId !== existingDonation.memberId) {
        return res.status(400).json({ success: false, message: "Cannot change the member of a donation" });
      }

      // Update the donation
      const updatedDonation = await storage.updateMemberDonation(donationId, validatedData);

      return res.status(200).json({ 
        success: true, 
        message: "Recurring donation updated successfully",
        donation: updatedDonation
      });
    } catch (error: any) {
      console.error("Error updating recurring donation:", error);
      return res.status(400).json({ success: false, message: error.message });
    }
  });

  // Member Payment History API Routes

  // Get payment history for a member
  app.get('/api/members/:memberId/payments', authenticate, async (req: AuthenticatedRequest, res) => {
    try {
      const memberId = parseInt(req.params.memberId);
      if (isNaN(memberId)) {
        return res.status(400).json({ success: false, message: "Invalid member ID" });
      }

      // Check if the member exists
      const member = await storage.getMember(memberId);
      if (!member) {
        return res.status(404).json({ success: false, message: "Member not found" });
      }

      // Check if user is admin or the member belongs to the authenticated user
      if (req.user?.role !== 'admin' && member.userId !== req.user?.id) {
        return res.status(403).json({ success: false, message: "Access denied" });
      }

      // Get the payment history
      const payments = await storage.getMemberPayments(memberId);

      return res.status(200).json({ 
        success: true, 
        payments
      });
    } catch (error: any) {
      console.error("Error fetching payment history:", error);
      return res.status(500).json({ success: false, message: error.message });
    }
  });

  // Get a specific payment by ID
  app.get('/api/member-payments/:id', authenticate, async (req: AuthenticatedRequest, res) => {
    try {
      const paymentId = parseInt(req.params.id);
      if (isNaN(paymentId)) {
        return res.status(400).json({ success: false, message: "Invalid payment ID" });
      }

      // Get the payment
      const payment = await storage.getMemberPayment(paymentId);
      if (!payment) {
        return res.status(404).json({ success: false, message: "Payment not found" });
      }

      // Get the member to check permissions
      const member = await storage.getMember(payment.memberId);
      if (!member) {
        return res.status(404).json({ success: false, message: "Member not found" });
      }

      // Check if user is admin or the member belongs to the authenticated user
      if (req.user?.role !== 'admin' && member.userId !== req.user?.id) {
        return res.status(403).json({ success: false, message: "Access denied" });
      }

      return res.status(200).json({ 
        success: true, 
        payment
      });
    } catch (error: any) {
      console.error("Error fetching payment:", error);
      return res.status(500).json({ success: false, message: error.message });
    }
  });

  // Create a new payment record
  app.post('/api/members/:memberId/payments', authenticate, async (req: AuthenticatedRequest, res) => {
    try {
      const memberId = parseInt(req.params.memberId);
      if (isNaN(memberId)) {
        return res.status(400).json({ success: false, message: "Invalid member ID" });
      }

      // Check if the member exists
      const member = await storage.getMember(memberId);
      if (!member) {
        return res.status(404).json({ success: false, message: "Member not found" });
      }

      // Only admins can create payment records manually
      if (req.user?.role !== 'admin') {
        return res.status(403).json({ success: false, message: "Access denied" });
      }

      // Validate payment data
      const validatedData = memberPaymentSchema.parse({
        ...req.body,
        memberId
      });

      // Create the payment record
      const payment = await storage.createMemberPayment(validatedData);

      return res.status(201).json({ 
        success: true, 
        message: "Payment record created successfully",
        payment
      });
    } catch (error: any) {
      console.error("Error creating payment record:", error);
      return res.status(400).json({ success: false, message: error.message });
    }
  });

  const httpServer = createServer(app);

  return httpServer;
}

server/storage.ts.content:
import { 
  users, type User, type InsertUser, 
  members, type Member, type InsertMember,
  memberBenefits, type MemberBenefit, type InsertMemberBenefit,
  memberDonations, type MemberDonation, type InsertMemberDonation,
  memberPayments, type MemberPayment, type InsertMemberPayment,
  type InsertContactMessage, type ContactMessage, 
  type InsertNewsletter, type Newsletter, 
  type InsertDonation, type Donation, 
  type InsertVolunteerApplication, type VolunteerApplication 
} from "@shared/schema";

// Modify the interface with any CRUD methods
// you might need
export interface IStorage {
  // User management
  getUser(id: number): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Member management
  getMember(id: number): Promise<Member | undefined>;
  getMemberByUserId(userId: number): Promise<Member | undefined>;
  createMember(member: InsertMember): Promise<Member>;
  updateMember(id: number, member: Partial<InsertMember>): Promise<Member>;
  getMembers(): Promise<Member[]>;

  // Member Benefits
  getMemberBenefits(membershipLevel: string): Promise<MemberBenefit[]>;
  createMemberBenefit(benefit: InsertMemberBenefit): Promise<MemberBenefit>;
  updateMemberBenefit(id: number, benefit: Partial<InsertMemberBenefit>): Promise<MemberBenefit>;
  getAllMemberBenefits(): Promise<MemberBenefit[]>;

  // Member Recurring Donations
  getMemberDonations(memberId: number): Promise<MemberDonation[]>;
  getMemberDonation(id: number): Promise<MemberDonation | undefined>;
  createMemberDonation(donation: InsertMemberDonation): Promise<MemberDonation>;
  updateMemberDonation(id: number, donation: Partial<InsertMemberDonation>): Promise<MemberDonation>;

  // Member Payment History
  getMemberPayments(memberId: number): Promise<MemberPayment[]>;
  getMemberPayment(id: number): Promise<MemberPayment | undefined>;
  createMemberPayment(payment: InsertMemberPayment): Promise<MemberPayment>;

  // Contact form
  createContactMessage(message: InsertContactMessage): Promise<ContactMessage>;
  getContactMessages(): Promise<ContactMessage[]>;

  // Newsletter
  createNewsletter(subscription: InsertNewsletter): Promise<Newsletter>;
  getNewsletterByEmail(email: string): Promise<Newsletter | undefined>;

  // Donations
  createDonation(donation: InsertDonation): Promise<Donation>;
  getDonations(): Promise<Donation[]>;

  // Volunteer applications
  createVolunteerApplication(application: InsertVolunteerApplication): Promise<VolunteerApplication>;
  getVolunteerApplications(): Promise<VolunteerApplication[]>;
}

export class MemStorage implements IStorage {
  private users: Map<number, User>;
  private members: Map<number, Member>;
  private memberBenefits: Map<number, MemberBenefit>;
  private memberDonations: Map<number, MemberDonation>;
  private memberPayments: Map<number, MemberPayment>;
  private contactMessages: Map<number, ContactMessage>;
  private newsletters: Map<number, Newsletter>;
  private donations: Map<number, Donation>;
  private volunteerApplications: Map<number, VolunteerApplication>;

  private currentUserId: number;
  private currentMemberId: number;
  private currentMemberBenefitId: number;
  private currentMemberDonationId: number;
  private currentMemberPaymentId: number;
  private currentContactMessageId: number;
  private currentNewsletterId: number;
  private currentDonationId: number;
  private currentVolunteerApplicationId: number;

  constructor() {
    this.users = new Map();
    this.members = new Map();
    this.memberBenefits = new Map();
    this.memberDonations = new Map();
    this.memberPayments = new Map();
    this.contactMessages = new Map();
    this.newsletters = new Map();
    this.donations = new Map();
    this.volunteerApplications = new Map();

    this.currentUserId = 1;
    this.currentMemberId = 1;
    this.currentMemberBenefitId = 1;
    this.currentMemberDonationId = 1;
    this.currentMemberPaymentId = 1;
    this.currentContactMessageId = 1;
    this.currentNewsletterId = 1;
    this.currentDonationId = 1;
    this.currentVolunteerApplicationId = 1;
  }

  // User methods
  async getUser(id: number): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.username === username,
    );
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.email === email,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = this.currentUserId++;
    const createdAt = new Date();
    const user: User = { 
      ...insertUser, 
      id, 
      createdAt, 
      email: insertUser.email || null,
      fullName: insertUser.fullName || null,
      role: insertUser.role || null 
    };
    this.users.set(id, user);
    return user;
  }

  // Member methods
  async getMember(id: number): Promise<Member | undefined> {
    return this.members.get(id);
  }

  async getMemberByUserId(userId: number): Promise<Member | undefined> {
    return Array.from(this.members.values()).find(
      (member) => member.userId === userId,
    );
  }

  async createMember(member: InsertMember): Promise<Member> {
    const id = this.currentMemberId++;
    const createdAt = new Date();
    const updatedAt = new Date();
    const newMember: Member = { 
      ...member, 
      id, 
      createdAt, 
      updatedAt, 
      membershipStatus: member.membershipStatus || 'active',
      address: member.address || null,
      city: member.city || null,
      state: member.state || null,
      country: member.country || null,
      postalCode: member.postalCode || null,
      phoneNumber: member.phoneNumber || null,
      preferences: member.preferences || {} 
    };
    this.members.set(id, newMember);
    return newMember;
  }

  async updateMember(id: number, memberUpdate: Partial<InsertMember>): Promise<Member> {
    const existingMember = await this.getMember(id);
    if (!existingMember) {
      throw new Error("Member not found");
    }

    const updatedMember: Member = {
      ...existingMember,
      ...memberUpdate,
      updatedAt: new Date()
    };

    this.members.set(id, updatedMember);
    return updatedMember;
  }

  async getMembers(): Promise<Member[]> {
    return Array.from(this.members.values());
  }

  // Member Benefits methods
  async getMemberBenefits(membershipLevel: string): Promise<MemberBenefit[]> {
    return Array.from(this.memberBenefits.values()).filter(
      (benefit) => benefit.membershipLevel === membershipLevel && benefit.isActive,
    );
  }

  async createMemberBenefit(benefit: InsertMemberBenefit): Promise<MemberBenefit> {
    const id = this.currentMemberBenefitId++;
    const createdAt = new Date();
    const newBenefit: MemberBenefit = { 
      ...benefit, 
      id, 
      createdAt, 
      isActive: benefit.isActive === undefined ? true : benefit.isActive 
    };
    this.memberBenefits.set(id, newBenefit);
    return newBenefit;
  }

  async updateMemberBenefit(id: number, benefitUpdate: Partial<InsertMemberBenefit>): Promise<MemberBenefit> {
    const existingBenefit = this.memberBenefits.get(id);
    if (!existingBenefit) {
      throw new Error("Benefit not found");
    }

    const updatedBenefit: MemberBenefit = {
      ...existingBenefit,
      ...benefitUpdate,
    };

    this.memberBenefits.set(id, updatedBenefit);
    return updatedBenefit;
  }

  async getAllMemberBenefits(): Promise<MemberBenefit[]> {
    return Array.from(this.memberBenefits.values());
  }

  // Contact message methods
  async createContactMessage(message: InsertContactMessage): Promise<ContactMessage> {
    const id = this.currentContactMessageId++;
    const createdAt = new Date();
    const contactMessage: ContactMessage = { 
      ...message, 
      id, 
      createdAt,
      subject: message.subject || null 
    };
    this.contactMessages.set(id, contactMessage);
    return contactMessage;
  }

  async getContactMessages(): Promise<ContactMessage[]> {
    return Array.from(this.contactMessages.values());
  }

  // Newsletter methods
  async createNewsletter(subscription: InsertNewsletter): Promise<Newsletter> {
    // Check if email already exists
    const existingSubscription = await this.getNewsletterByEmail(subscription.email);
    if (existingSubscription) {
      throw new Error("Email already subscribed to newsletter");
    }

    const id = this.currentNewsletterId++;
    const createdAt = new Date();
    const newsletter: Newsletter = { ...subscription, id, createdAt };
    this.newsletters.set(id, newsletter);
    return newsletter;
  }

  async getNewsletterByEmail(email: string): Promise<Newsletter | undefined> {
    return Array.from(this.newsletters.values()).find(
      (newsletter) => newsletter.email === email,
    );
  }

  // Donation methods
  async createDonation(donation: InsertDonation): Promise<Donation> {
    const id = this.currentDonationId++;
    const createdAt = new Date();

    // Simply use the amount as-is since it should be a string type
    // or convert it if somehow it's a number
    const amountValue = typeof donation.amount === 'number' ? 
      String(donation.amount) : (donation.amount || '0');

    const newDonation: Donation = { 
      ...donation, 
      id, 
      createdAt,
      amount: amountValue,
      address: donation.address || null,
      city: donation.city || null,
      country: donation.country || null,
      message: donation.message || null
    };
    this.donations.set(id, newDonation);
    return newDonation;
  }

  async getDonations(): Promise<Donation[]> {
    return Array.from(this.donations.values());
  }

  // Volunteer application methods
  async createVolunteerApplication(application: InsertVolunteerApplication): Promise<VolunteerApplication> {
    const id = this.currentVolunteerApplicationId++;
    const createdAt = new Date();
    const volunteerApplication: VolunteerApplication = { 
      ...application, 
      id, 
      createdAt,
      message: application.message || null,
      city: application.city || null,
      country: application.country || null,
      phone: application.phone || null,
      availability: application.availability || null,
      experience: application.experience || null
    };
    this.volunteerApplications.set(id, volunteerApplication);
    return volunteerApplication;
  }

  async getVolunteerApplications(): Promise<VolunteerApplication[]> {
    return Array.from(this.volunteerApplications.values());
  }

  // Member Recurring Donation methods
  async getMemberDonations(memberId: number): Promise<MemberDonation[]> {
    return Array.from(this.memberDonations.values()).filter(
      (donation) => donation.memberId === memberId
    );
  }

  async getMemberDonation(id: number): Promise<MemberDonation | undefined> {
    return this.memberDonations.get(id);
  }

  async createMemberDonation(donation: InsertMemberDonation): Promise<MemberDonation> {
    const id = this.currentMemberDonationId++;
    const createdAt = new Date();
    const updatedAt = new Date();

    // Format amount value
    const amountValue = typeof donation.amount === 'number' ? 
      String(donation.amount) : (donation.amount || '0');

    const newDonation: MemberDonation = { 
      ...donation, 
      id, 
      createdAt, 
      updatedAt,
      amount: amountValue,
      status: donation.status || 'active',
      paymentMethod: donation.paymentMethod || 'card',
      transactionId: donation.transactionId || null,
      notes: donation.notes || null
    };

    this.memberDonations.set(id, newDonation);
    return newDonation;
  }

  async updateMemberDonation(id: number, donationUpdate: Partial<InsertMemberDonation>): Promise<MemberDonation> {
    const existingDonation = this.memberDonations.get(id);
    if (!existingDonation) {
      throw new Error("Member donation not found");
    }

    // Format amount value if it exists in the update
    let updatedAmount = existingDonation.amount;
    if (donationUpdate.amount !== undefined) {
      updatedAmount = typeof donationUpdate.amount === 'number' ? 
        String(donationUpdate.amount) : donationUpdate.amount;
    }

    const updatedDonation: MemberDonation = {
      ...existingDonation,
      ...donationUpdate,
      amount: updatedAmount,
      updatedAt: new Date()
    };

    this.memberDonations.set(id, updatedDonation);
    return updatedDonation;
  }

  // Member Payment History methods
  async getMemberPayments(memberId: number): Promise<MemberPayment[]> {
    return Array.from(this.memberPayments.values())
      .filter(payment => payment.memberId === memberId)
      .sort((a, b) => {
        // Sort by payment date, most recent first
        return new Date(b.paymentDate).getTime() - new Date(a.paymentDate).getTime();
      });
  }

  async getMemberPayment(id: number): Promise<MemberPayment | undefined> {
    return this.memberPayments.get(id);
  }

  async createMemberPayment(payment: InsertMemberPayment): Promise<MemberPayment> {
    const id = this.currentMemberPaymentId++;
    const createdAt = new Date();

    // Format amount value
    const amountValue = typeof payment.amount === 'number' ? 
      String(payment.amount) : (payment.amount || '0');

    const newPayment: MemberPayment = { 
      id, 
      createdAt,
      memberId: payment.memberId,
      donationId: payment.donationId || null,
      amount: amountValue,
      paymentDate: payment.paymentDate,
      status: payment.status || 'completed',
      paymentMethod: payment.paymentMethod,
      transactionId: payment.transactionId || null,
      receiptUrl: payment.receiptUrl || null,
      notes: payment.notes || null
    };

    this.memberPayments.set(id, newPayment);
    return newPayment;
  }
}

export const storage = new MemStorage();

