import { ReactNode } from 'react';
import { motion } from 'framer-motion';
import { cn } from '@/lib/utils';

interface AnimatedButtonProps {
  children: ReactNode;
  variant?: 'primary' | 'secondary' | 'outline' | 'ghost';
  size?: 'sm' | 'md' | 'lg';
  className?: string;
  onClick?: () => void;
  type?: 'button' | 'submit' | 'reset';
  disabled?: boolean;
}

const buttonVariants = {
  idle: { scale: 1 },
  hover: { 
    scale: 1.05,
    boxShadow: "0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05)",
  },
  tap: { scale: 0.98 }
};

const AnimatedButton = ({ 
  children, 
  className,
  variant = 'primary',
  size = 'md',
  onClick,
  type = 'button',
  disabled = false
}: AnimatedButtonProps) => {
  // Button variants
  const variantStyles = {
    primary: 'bg-orange-500 hover:bg-orange-600 text-white shadow-md',
    secondary: 'bg-gray-800 hover:bg-gray-900 text-white shadow-md', 
    outline: 'bg-transparent border-2 border-orange-500 text-orange-500 hover:bg-orange-50',
    ghost: 'bg-transparent hover:bg-orange-50 text-orange-500'
  };

  // Button sizes
  const sizeStyles = {
    sm: 'px-4 py-2 text-sm',
    md: 'px-6 py-3 text-base',
    lg: 'px-8 py-4 text-lg'
  };

  return (
    <motion.button
      className={cn(
        'font-bold rounded-lg transition-colors',
        variantStyles[variant],
        sizeStyles[size],
        disabled ? 'opacity-50 cursor-not-allowed' : '',
        className
      )}
      variants={buttonVariants}
      initial="idle"
      whileHover={disabled ? undefined : "hover"}
      whileTap={disabled ? undefined : "tap"}
      onClick={onClick}
      type={type}
      disabled={disabled}
    >
      {children}
    </motion.button>
  );
};

export default AnimatedButton;

import { ReactNode } from 'react';
import { motion, MotionProps } from 'framer-motion';

interface AnimatedSectionProps extends MotionProps {
  children: ReactNode;
  delay?: number;
  className?: string;
}

// Fade-in animation for sections that come into view
export const FadeInSection = ({ 
  children, 
  delay = 0, 
  className = "", 
  ...props 
}: AnimatedSectionProps) => {
  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ 
        duration: 0.6, 
        delay, 
        ease: "easeOut" 
      }}
      className={className}
      {...props}
    >
      {children}
    </motion.div>
  );
};

// Slide-in animation from the side
export const SlideInSection = ({ 
  children,  
  delay = 0,
  className = "",
  ...props
}: AnimatedSectionProps & { direction?: 'left' | 'right' }) => {
  const direction = props.direction || 'left';
  const initialX = direction === 'left' ? -50 : 50;

  return (
    <motion.div
      initial={{ opacity: 0, x: initialX }}
      animate={{ opacity: 1, x: 0 }}
      transition={{ 
        type: "spring",
        stiffness: 100,
        damping: 15,
        delay
      }}
      className={className}
    >
      {children}
    </motion.div>
  );
};

// Scale-up animation for elements that need to grow into view
export const ScaleUpSection = ({ 
  children, 
  delay = 0,
  className = "",
  ...props 
}: AnimatedSectionProps) => {
  return (
    <motion.div
      initial={{ opacity: 0, scale: 0.8 }}
      animate={{ opacity: 1, scale: 1 }}
      transition={{ 
        type: "spring",
        stiffness: 300,
        damping: 20,
        delay
      }}
      className={className}
      {...props}
    >
      {children}
    </motion.div>
  );
};

// Animation for staggered children
export const StaggerContainer = ({ 
  children, 
  delay = 0,
  staggerDelay = 0.1,
  className = "",
  ...props 
}: AnimatedSectionProps & { staggerDelay?: number }) => {
  return (
    <motion.div
      initial="hidden"
      animate="visible"
      variants={{
        hidden: { opacity: 0 },
        visible: {
          opacity: 1,
          transition: {
            delay,
            staggerChildren: staggerDelay
          }
        }
      }}
      className={className}
      {...props}
    >
      {children}
    </motion.div>
  );
};

// Child component for StaggerContainer
export const StaggerItem = ({ 
  children, 
  className = "",
  ...props 
}: AnimatedSectionProps) => {
  return (
    <motion.div
      variants={{
        hidden: { opacity: 0, y: 20 },
        visible: { 
          opacity: 1, 
          y: 0,
          transition: {
            type: "spring",
            stiffness: 100,
            damping: 10
          }
        }
      }}
      className={className}
      {...props}
    >
      {children}
    </motion.div>
  );
};

// Pulse animation for attention-grabbing elements
